public class Ocho {

	public static void main(String[] args) {

		// Declaro el numero
		String num = "";

		int aux, aux_copia, a, b, c, d, e, max = 0, producto = 0;

		for (int i = 0; i < 996; i++) {

			// Se toman los 5 numeros de la serie
			aux = Integer.parseInt(num.substring(i, (i + 5)));

			// Copia de los 5
			aux_copia = aux;

			// Se calcula el producto de los digitos
			a = aux_copia % 10;
			aux_copia = aux_copia / 10;
			b = aux_copia % 10;
			aux_copia = aux_copia / 10;
			c = aux_copia % 10;
			aux_copia = aux_copia / 10;
			d = aux_copia % 10;
			aux_copia = aux_copia / 10;
			e = aux_copia % 10;
			aux_copia = aux_copia / 10;
			producto = a * b * c * d * e;

			// Se comprueba si el producto de los digitos es mayor al maximo
			if (producto > max) {
				max = producto;
			}
			
			System.out.println("\n"+i);
			System.out.println(aux);
			System.out.println(max);
		}
	}// fin main
}
